import React from 'react';

import s from './OnOff.module.css';

interface Props {
  checked: boolean;
}

export function OnOff({checked}: Props) {
  const onChecked =  ' ' + checked && s.on;
  const offChecked =  ' ' + !checked && s.off;

  return (
    <div className={s.wrap}>
      <div className={s.block  + onChecked}>On</div>
      <div className={s.block + offChecked}>Off</div>
      <div className={s.circle + ' ' + checked ? s.on : s.off}></div>
    </div>
  );
};
