import React, {useState} from 'react';

type AccordionPropsTitle = {
  titleValue: string;
  collapsed: boolean;
  setCollapsed: () => void;
}

export function Accordion(props: AccordionPropsTitle) {
  const [collapsed, setCollapsed] = useState(false)

  return (
    <>
      <AccordionTitle titleValue={props.titleValue} collapsed={collapsed} setCollapsed={setCollapsed}/>
      {
        collapsed &&  <AccordionBody/>
      }
    </>
  );
};

export function AccordionTitle(props: AccordionPropsTitle) {
  return (
    <button onClick={()=> props.setCollapsed(!(props.collapsed))} >
      <h3>
        {props.titleValue}
      </h3>
    </button>

  );
};

export function AccordionBody() {
  return (
    <div>
      <ul>
        <li></li>
        <li></li>
        <li></li>
      </ul>
    </div>
  );
};
