import React from 'react';

import './OnOff.css';

interface Props {
  checked: boolean;
}

export function OnOff({checked}: Props) {
  const onChecked =  checked && ' on';
  const offChecked =   !checked && ' off';

  return (
    <div className='wrap'>
      <div className={`block ${onChecked}`}>On</div>
      <div className={`block + ${offChecked}`}>Off</div>
      <div className={`circle + ' ' + ${checked ? 'on' : 'off'}`}></div>
    </div>
  );
};
