import React, {useState} from 'react';

// type RatingPropsType = {
//   value: 1 | 2 | 3 | 4 | 5;
// }

export function Rating() {
  const [value, setValue] = useState(0)
  return (
    <div>
      <Star  selected={value ===1} setValue={()=>setValue(value)}/>
      {/*<Star selected={value ===2}/>*/}
      {/*<Star selected={value ===3}/>*/}
      {/*<Star selected={value ===4}/>*/}
      {/*<Star selected={value ===5}/>*/}

      </div>
  );
};

type StarsProps = {
  selected: boolean;
  setValue: () => void;
  value: number;

}

export function Star(props: StarsProps) {
  return (
    <button onClick={() => props.setValue()}> <span style={props.selected ? {fontWeight: '700'} : undefined}>star </span></button>
  );
};
